import React, { useState } from "react";
// import Todo from "./components/Todo.js";
import "./App.css";

function TodoForm({ addTodo }){
  const [value, setValue] = useState('')

  const handleSubmit = e => {
    e.preventDefault();
    if(!value){
      return
    }
    addTodo(value);
    setValue('');
  }

  return(
    <form onSubmit={handleSubmit}>
      <input type="text" className="input" placeholder="Add TODO..." value={value} onChange={ (e) => setValue(e.target.value) }/>
    </form>
  )
}

function Todo({ todo, index, completeTask }) {
  return (
    <div className="todo" style={{textDecoration: todo.isCompleted ? 'line-through' : ''}}>
      {todo.text}
      <div className="btn-container">
        <button onClick={ ()=> completeTask(index) } >
          COMPLETE
        </button>
        <button onClick={ ()=> deleteTask(index) } >
          DELETE
        </button>

      </div>
    </div>
  );
}

function App() {
  const [todos, setTodos] = useState([
    {
      text: "Learn about React",
      isCompleted: false
    },
    {
      text: "Make ToDo App",
      isCompleted: false
    },
    {
      text: "Get $$$",
      isCompleted: false
    }
  ]);

  const addTodo = text => {
    const newTodos = [...todos, {text}];
    setTodos(newTodos)
  }

  const completeTask = (index) => {
    const newTodos=[...todos];
    newTodos[index].isCompleted = !newTodos[index].isCompleted
    setTodos(newTodos)
  }

  const deleteTask = (index) => {
    const newTodos=[...todos];
    newTodos.splice(index, 1)
    setTodos(newTodos)
  }

  return (
    <div className="app">
      <div className="todo-list">
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} completeTask={completeTask} deleteTask={deleteTask}/>
        ))}
        <TodoForm addTodo={addTodo} />
      </div>
    </div>
  );
}

export default App;
